// src/App.js
import React, { useState } from 'react';
import MyPage from './MyPage';
import Options from './Options';
import './App.css'; // Import your CSS file for global styles

function App() {
    const [selectedOption, setSelectedOption] = useState(null);

    const handleSelectOption = (option) => {
        setSelectedOption(option);
    };

    const pageStyles = {
        backgroundColor: '#3498db',
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#020405', // Set text color to white for better visibility on a blue background
    };

    return (
        <div style={pageStyles}>
            <header>
                <h1>Kaligiri Sairam
                </h1>

            </header>

            <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '20px' }}>
                <div>
                    <Options onSelectOption={handleSelectOption} />
                </div>
                <div>
                    {selectedOption ? (
                        <div>
                            <h2>{selectedOption}</h2>
                            <p>Details for {selectedOption}</p>
                        </div>
                    ) : (
                        <MyPage />
                    )}
                </div>
            </div>
        </div>
    );
}

export default App;
